$(document).ready(function () {
  var tableDisable = $(".dataex-aip-enable-disable").DataTable({
    dom: "Bfrtip",
    select: !0,
    buttons: [
      {
        text: "Row selected data",
        action: function (e, dt, node, config) {
          alert("Row data: " + JSON.stringify(dt.row({ selected: !0 }).data()));
        },
        enabled: !1,
      },
      {
        text: "Count rows selected",
        action: function (e, dt, node, config) {
          alert("Rows: " + dt.rows({ selected: !0 }).count());
        },
        enabled: !1,
      },
    ],
  });
  tableDisable.on("select", function () {
    var selectedRows = tableDisable.rows({ selected: !0 }).count();
    tableDisable.button(0).enable(1 === selectedRows),
      tableDisable.button(1).enable(selectedRows > 0);
  }),
    $(".dataex-aip-dynamic").DataTable({
      dom: "Bfrtip",
      buttons: [
        {
          text: "My button",
          action: function (e, dt, node, config) {
            this.text("My button (" + config.counter + ")"), config.counter++;
          },
          counter: 1,
        },
      ],
    });
  var counter = 1;
  $(".dataex-aip-dynamically").DataTable({
    dom: "Bfrtip",
    buttons: [
      {
        text: "Add new button",
        action: function (e, dt, node, config) {
          dt.button().add(1, {
            text: "Button " + counter++,
            action: function () {
              this.remove();
            },
          });
        },
      },
    ],
  });
  var table = $(".dataex-aip-group").DataTable();
  new $.fn.dataTable.Buttons(table, {
    buttons: [
      { text: "Button 1", action: function (e, dt, node, conf) {} },
      { text: "Button 2", action: function (e, dt, node, conf) {} },
    ],
  }),
    table.buttons(0, null).container().prependTo(table.table().container());
});
