$(document).ready(function () {
  $(".dataex-visibility-print").DataTable({
    dom: "Bfrtip",
    buttons: ["print"],
  }),
    $(".dataex-visibility-message").DataTable({
      dom: "Bfrtip",
      buttons: [
        {
          extend: "print",
          message:
            "This print was produced using the Print button for DataTables",
        },
      ],
    }),
    $(".dataex-visibility-selector").DataTable({
      dom: "Bfrtip",
      buttons: [
        { extend: "print", exportOptions: { columns: ":visible" } },
        "colvis",
      ],
      columnDefs: [{ targets: -1, visible: !1 }],
    }),
    $(".dataex-visibility-rowselector").DataTable({
      dom: "Bfrtip",
      buttons: [
        { extend: "print", text: "Print all" },
        {
          extend: "print",
          text: "Print selected",
          exportOptions: { modifier: { selected: !0 } },
        },
      ],
      select: !0,
    }),
    $(".dataex-visibility-disable").DataTable({
      dom: "Bfrtip",
      buttons: [{ extend: "print", autoPrint: !1 }],
    }),
    $(".dataex-visibility-customisation").DataTable({
      dom: "Bfrtip",
      buttons: [
        {
          extend: "print",
          customize: function (win) {
            $(win.document.body)
              .css("font-size", "10pt")
              .prepend(
                '<img src="http://datatables.net/media/images/logo-fade.png" style="position:absolute; top:0; left:0;" />'
              ),
              $(win.document.body)
                .find("table")
                .addClass("compact")
                .css("font-size", "inherit");
          },
        },
      ],
    });
});
