$(document).ready(function () {
  $(".dataex-key-basic").DataTable({ keys: !0 });
  var events = $(".dt-events-log"),
    tableEvents = $(".dataex-key-events").DataTable({ keys: !0 });
  tableEvents
    .on("key", function (e, datatable, key, cell, originalEvent) {
      events.prepend(
        "<div>Key press: " + key + " for cell <i>" + cell.data() + "</i></div>"
      );
    })
    .on("key-focus", function (e, datatable, cell) {
      events.prepend("<div>Cell focus: <i>" + cell.data() + "</i></div>");
    })
    .on("key-blur", function (e, datatable, cell) {
      events.prepend("<div>Cell blur: <i>" + cell.data() + "</i></div>");
    });
  $(".dataex-key-scrolling").DataTable({ scrollY: 300, paging: !1, keys: !0 });
  $(".dataex-key-State").DataTable({ keys: !0, stateSave: !0 }),
    $(".dataex-key-customstyling").DataTable({ keys: !0 });
});
