$(document).ready(function () {
  var tableEvent =
    ($(".dataex-rowre-basic").DataTable({ rowReorder: !0 }),
    $(".dataex-rowre-restricted").DataTable({
      rowReorder: !0,
      columnDefs: [
        { orderable: !0, className: "reorder", targets: 0 },
        { orderable: !1, targets: "_all" },
      ],
    }),
    $(".dataex-rowre-mobilesupport").DataTable({
      rowReorder: { selector: "td:nth-child(2)" },
      responsive: !0,
    }),
    $(".dataex-rowre-fullrow").DataTable({
      rowReorder: { selector: "tr" },
      columnDefs: [{ targets: 0, visible: !1 }],
    }),
    $(".dataex-rowre-event").DataTable({ rowReorder: !0 }));
  tableEvent.on("row-reorder", function (e, diff, edit) {
    for (
      var result =
          "Reorder started on row: " + edit.triggerRow.data()[1] + "<br>",
        i = 0,
        ien = diff.length;
      i < ien;
      i++
    ) {
      var rowData = tableEvent.row(diff[i].node).data();
      result +=
        rowData[1] +
        " updated to be in position " +
        diff[i].newData +
        " (was " +
        diff[i].oldData +
        ")<br>";
    }
    $(".dt-events-log").html("Event result:<br>" + result);
  });
  $(".dataex-rowre-scrolling").DataTable({
    rowReorder: !0,
    scrollY: 300,
    paging: !1,
  }),
    $(".dataex-rowre-horizontal").DataTable({ rowReorder: { snapX: 10 } }),
    $(".dataex-select-buttons").DataTable({
      dom: "Bfrtip",
      buttons: [
        "selected",
        "selectedSingle",
        "selectAll",
        "selectNone",
        "selectRows",
        "selectColumns",
        "selectCells",
      ],
      select: !0,
    });
});
